import React, { useState, useEffect } from 'react';
import EmployeeForm from './EmployeeForm';
import { employeeService } from '../../services/employeeService';
import './Employee.css';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchEmployees();
    setShowForm(false);
    setEditingEmployee(null);
  }, []);

  const fetchEmployees = async () => {
    try {
      setLoading(true);
      const data = await employeeService.getEmployees();
      setEmployees(data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAdd = () => {
    setEditingEmployee(null);
    setShowForm(true);
  };

  const handleEdit = (employee) => {
    setEditingEmployee(employee);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    try {
      await employeeService.deleteEmployee(id);
      fetchEmployees();
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  const handleSave = async (employeeData) => {
    try {
      if (editingEmployee) {
        await employeeService.editEmployee(editingEmployee.id, employeeData);
      } else {
        await employeeService.addEmployee(employeeData);
      }
      fetchEmployees();
      setShowForm(false);
      setEditingEmployee(null);
    } catch (error) {
      console.error('Error saving employee:', error);
    }
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingEmployee(null);
  };

  // Reset form when component receives focus
  useEffect(() => {
    const resetForm = () => {
      setShowForm(false);
      setEditingEmployee(null);
    };

    window.addEventListener('focus', resetForm);
    return () => window.removeEventListener('focus', resetForm);
  }, []);

  const SkeletonRow = () => (
    <tr className="skeleton-row">
      <td><div className="skeleton skeleton-text"></div></td>
      <td><div className="skeleton skeleton-text"></div></td>
      <td><div className="skeleton skeleton-text"></div></td>
      <td><div className="skeleton skeleton-text"></div></td>
      <td>
        <div className="skeleton-actions">
          <div className="skeleton skeleton-button"></div>
          <div className="skeleton skeleton-button"></div>
        </div>
      </td>
    </tr>
  );



  return (
    <div className="employee-container">
      <div className="employee-header">
        <h2>Employee Management</h2>
        {!showForm && (
          <button className="btn-primary" onClick={handleAdd}>Add Employee</button>
        )}
      </div>

      {showForm ? (
        <EmployeeForm 
          employee={editingEmployee}
          onSave={handleSave}
          onCancel={handleCancel}
        />
      ) : (
        <div className="table-container">
          <div className="table-header">
            <div className="table-info">
              <span className="employee-count">{employees.length} Employees</span>
            </div>
          </div>

          <div className="table-wrapper">
            <table className="employee-table">
              <thead>
                <tr>
                  <th>Employee ID</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Department</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  Array(5).fill().map((_, index) => <SkeletonRow key={index} />)
                ) : employees.length > 0 ? employees.map(employee => (
                  <tr key={employee.id} className="employee-row">
                    <td className="employee-id">{employee.employeeId}</td>
                    <td className="employee-name">{employee.name}</td>
                    <td className="employee-email">{employee.email}</td>
                    <td className="employee-department">
                      <span className="department-badge">{employee.department}</span>
                    </td>
                    <td className="actions-cell">
                      <div className="action-buttons">
                        <button className="btn-edit" onClick={() => handleEdit(employee)}>Edit</button>
                        <button className="btn-delete" onClick={() => handleDelete(employee.id)}>Delete</button>
                      </div>
                    </td>
                  </tr>
                )) : (
                  <tr>
                    <td colSpan="5" className="no-data">No employees found</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default EmployeeList;
