import React, { useState, useEffect } from 'react';
import EmployeeForm from './EmployeeForm';
import AttendanceModal from './AttendanceModal';
import { employeeService } from '../../services/employeeService';
import './Employee.css';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [showAttendanceModal, setShowAttendanceModal] = useState(false);
  const [selectedEmployeeForAttendance, setSelectedEmployeeForAttendance] = useState(null);
  const [isAdmin, setIsAdmin] = useState(true);

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const data = await employeeService.getEmployees();
      setEmployees(data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const handleAdd = () => {
    setEditingEmployee(null);
    setShowForm(true);
  };

  const handleEdit = (employee) => {
    setEditingEmployee(employee);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    try {
      await employeeService.deleteEmployee(id);
      fetchEmployees();
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  const handleSave = async (employeeData) => {
    try {
      if (editingEmployee) {
        await employeeService.editEmployee(editingEmployee.id, employeeData);
      } else {
        await employeeService.addEmployee(employeeData);
      }
      fetchEmployees();
      setShowForm(false);
      setEditingEmployee(null);
    } catch (error) {
      console.error('Error saving employee:', error);
    }
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingEmployee(null);
  };

  const handleUpdateAttendance = (employee) => {
    setSelectedEmployeeForAttendance(employee);
    setShowAttendanceModal(true);
  };

  const handleCloseAttendanceModal = () => {
    setShowAttendanceModal(false);
    setSelectedEmployeeForAttendance(null);
  };

  return (
    <div className="employee-container">
      <h2>Employee Management</h2>

      {showForm ? (
        <EmployeeForm 
          employee={editingEmployee}
          onSave={handleSave}
          onCancel={handleCancel}
        />
      ) : (
        <>
          <div className="button-group">
            <button className="btn-primary" onClick={handleAdd}>Add Employee</button>
          </div>

          <table className="employee-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Department</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {employees.length > 0 ? employees.map(employee => (
                <tr key={employee.id}>
                  <td>{employee.name}</td>
                  <td>{employee.email}</td>
                  <td>{employee.department}</td>
                  <td>
                    <button className="btn-edit" onClick={() => handleEdit(employee)}>Edit</button>
                    <button className="btn-delete" onClick={() => handleDelete(employee.id)}>Delete</button>
                    {isAdmin && (
                      <button className="btn-attendance" onClick={() => handleUpdateAttendance(employee)}>Update Attendance</button>
                    )}
                  </td>
                </tr>
              )) : (
                <tr>
                  <td colSpan="4">No employees found</td>
                </tr>
              )}
            </tbody>
          </table>
        </>
      )}

      {showAttendanceModal && (
        <AttendanceModal 
          employee={selectedEmployeeForAttendance}
          onClose={handleCloseAttendanceModal}
        />
      )}
    </div>
  );
};

export default EmployeeList;
