import React, { useState, useEffect } from 'react';
import EmployeeForm from './EmployeeForm';
import { employeeService } from '../../services/employeeService';
import './Employee.css';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [isAdmin, setIsAdmin] = useState(true); // Set to true for demo, implement proper auth

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const data = await employeeService.getEmployees();
      setEmployees(data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const handleAdd = () => {
    setEditingEmployee(null);
    setShowForm(true);
  };

  const handleEdit = (employee) => {
    setEditingEmployee(employee);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    try {
      await employeeService.deleteEmployee(id);
      fetchEmployees();
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  const handleSave = async (employeeData) => {
    try {
      if (editingEmployee) {
        await employeeService.editEmployee(editingEmployee.id, employeeData);
      } else {
        await employeeService.addEmployee(employeeData);
      }
      fetchEmployees();
      setShowForm(false);
      setEditingEmployee(null);
    } catch (error) {
      console.error('Error saving employee:', error);
    }
  };

  const handleAttendanceUpdate = async (employeeId, status) => {
    try {
      await employeeService.updateAttendance(employeeId, status);
      fetchEmployees();
    } catch (error) {
      console.error('Error updating attendance:', error);
    }
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingEmployee(null);
  };

  return (
    <div className="employee-container">
      <h2>Employee Management</h2>

      {!showForm ? (
        <>
          <button className="btn-primary" onClick={handleAdd}>Add Employee</button>

          <table className="employee-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Department</th>
                {isAdmin && <th>Today's Attendance</th>}
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {employees.length > 0 ? employees.map(employee => (
                <tr key={employee.id}>
                  <td>{employee.name}</td>
                  <td>{employee.email}</td>
                  <td>{employee.department}</td>
                  {isAdmin && (
                    <td>
                      <div className="attendance-container">
                        <span className="attendance-date">{new Date().toLocaleDateString()}</span>
                        <select 
                          value={employee.todayAttendance || 'Present'} 
                          onChange={(e) => handleAttendanceUpdate(employee.id, e.target.value)}
                          className="attendance-select"
                        >
                          <option value="Present">Present</option>
                          <option value="Absent">Absent</option>
                        </select>
                      </div>
                    </td>
                  )}
                  <td>
                    <button className="btn-edit" onClick={() => handleEdit(employee)}>Edit</button>
                    <button className="btn-delete" onClick={() => handleDelete(employee.id)}>Delete</button>
                  </td>
                </tr>
              )) : (
                <tr>
                  <td colSpan={isAdmin ? "5" : "4"}>No employees found</td>
                </tr>
              )}
            </tbody>
          </table>
        </>
      ) : (
        <EmployeeForm 
          employee={editingEmployee}
          onSave={handleSave}
          onCancel={handleCancel}
        />
      )}
    </div>
  );
};

export default EmployeeList;
